function loadNewsData(item) {
    var feed = Feed.gettingFeedsList(item.title, item.title_image, item.shortDescription, item.fullDescription, item.titleImageURL, item.descriptionImageURL, item.articleType, item.publishedDate, item.createdAt, item.updatedAt)
    var feedScript = "'<table border=1>' + '<td>' + '<h1>' + item.title + '</h1>' + '<p>' + item.short_description + '</p>' + '<p>' + item.full_description + '</p>' + '<img src= '+ item.description_image_url +' + alt=\"Image\" + style=\"width:auto;height:auto;\">' + '</td>' + '</table>';"
    var drawable = new FeedDrawable();
    drawable.script = feedScript
    addToView(drawable)
}

